pragma solidity ^0.8.19;

contract ToAttackFrontrunning {

    bytes32 public rightHash = 0x54686973206973207468652070617373776f726420746f20756e6c6f636b2072;
    bool alreadyFund = false;



    function setResponse(string memory _yourAnswer)public{

        if(rightHash == keccak256(abi.encodePacked(_yourAnswer)) && !alreadyFund){
            alreadyFund = true;
            payable(msg.sender).transfer(address(this).balance);
        }else{
            revert("Already Fund or Wrong Answer!");
        }
    }


    receive()external payable{}
}

